<html lang="en"><head><meta charset="utf-8" /><title>jQuery UI Autocomplete - Multiple values</title><link rel="stylesheet" href="jquery-ui.css" /><script src="jquery-1.9.1.js"></script><script src="jquery-ui.js"></script><link rel="stylesheet" href="/resources/demos/style.css" /><script>$(function() {var availableTags = ["ActionScript","AppleScript","Asp","BASIC","C","C++","Clojure","COBOL","ColdFusion","Erlang","Fortran","Groovy","Haskell","Java","JavaScript","Lisp","Perl","PHP","Python","Ruby","Scala","Scheme"];function split( val ) {return val.split( /,\s*/ );}function extractLast( term ) {return split( term ).pop();}$( "#tags" )// don't navigate away from the field on tab when selecting an item.bind( "keydown", function( event ) {if ( event.keyCode === $.ui.keyCode.TAB &&$( this ).data( "ui-autocomplete" ).menu.active ) {event.preventDefault();}}).autocomplete({minLength: 0,source: function( request, response ) {// delegate back to autocomplete, but extract the last termresponse( $.ui.autocomplete.filter(availableTags, extractLast( request.term ) ) );},focus: function() {// prevent value inserted on focusreturn false;},select: function( event, ui ) {var terms = split( this.value );// remove the current inputterms.pop();// add the selected itemterms.push( ui.item.value );// add placeholder to get the comma-and-space at the endterms.push( "" );this.value = terms.join( ", " );return false;}});});</script></head><body><div class="ui-widget"><label for="tags">Tag programming languages: </label><input id="tags" size="50" /></div></body></html>