<?phprequire("source1.php");?><html lang="en"><head><meta charset="utf-8" /><title>jQuery UI Autocomplete - Multiple values</title><link rel="stylesheet" href="jquery-ui.css" /><script src="jquery-1.9.1.js"></script><script src="jquery-ui.js"></script><link rel="stylesheet" href="/resources/demos/style.css" /><script>$(function() {function split( val ) {return val.split( /,\s*/ );}function extractLast( term ) {return split( term ).pop();}$( "#tags" )// don't navigate away from the field on tab when selecting an item.bind( "keydown", function( event ) {if ( event.keyCode === $.ui.keyCode.TAB &&$( this ).data( "ui-autocomplete" ).menu.active ) {event.preventDefault();}}).autocomplete({source: function( request, response ) {$.getJSON( "search.1php", {term: extractLast( request.term )}, response );},});</script></head><body><div class="ui-widget"><label for="tags">Tag programming languages: </label><input id="tags" size="50" /></div></body></html>